import { useEffect, useState } from 'react';
import { io } from 'socket.io-client';

const socket = io('http://localhost:5000'); // adjust if needed

export default function Chat() {
  const [username, setUsername] = useState('');
  const [tempUsername, setTempUsername] = useState(''); // for the input field
  const [message, setMessage] = useState('');
  const [messages, setMessages] = useState<string[]>([]);

  useEffect(() => {
    socket.on("receive_message", (msg) => {
      setMessages((prev) => [...prev, msg]);
    });

    return () => {
      socket.off("receive_message");
    };
  }, []);

  const sendMessage = () => {
    if (message.trim() !== '' && username.trim() !== '') {
      socket.emit('send_message', `${username}: ${message}`);
      setMessage('');
    } else if (username.trim() === '') {
      alert('Please set a username first!');
    }
  };

  const setName = () => {
    if (tempUsername.trim() !== '') {
      setUsername(tempUsername.trim());
      setTempUsername('');
    }
  };

  return (
    <div className="chat-container">
      <div className="sidebar">
        <img src="your-image-path.png" alt="Sidebar logo" />
        <div style={{ marginTop: '1rem', color: 'white' }}>
          <input
            type="text"
            placeholder="Enter your name"
            value={tempUsername}
            onChange={e => setTempUsername(e.target.value)}
          />
          <button onClick={setName}>Set Name</button>
          <p>Your name: <strong>{username || 'Not set'}</strong></p>
        </div>
      </div>

      <div className="chat-main">
        <div className="messages">
          {messages.map((msg, i) => (
            <div key={i} className="message">{msg}</div>
          ))}
        </div>
        <div className="input-bar">
          <input
            type="text"
            placeholder="Send a message"
            value={message}
            onChange={(e) => setMessage(e.target.value)}
          />
          <button onClick={sendMessage}>Send</button>
        </div>
      </div>
    </div>
  );
}
